(137, 'El secreto del tanque de aqua', 'María Inés Falconi', 'Editorial desconocida', '9789870413905', '2010', 'While visiting their grandmother one day, Lucas y Rocío are transported back to 1810 at the beginning of Argentina\'s fight for independence.', 'Ficción Niños', 1, 1, 'no_hay_portada.jpg'),
(138, 'El Libro de Los Chicos Enamorados', 'Elsa Bornemann', 'Editorial desconocida', '9789870402282', '2006-04-19', 'No disponible', 'Ficción Niños', 1, 1, 'http://books.google.com/books/content?id=XASWAAAACAAJ&printsec=frontcover&img=1&zoom=1&source=gbs_api'),
