(218, 'Otra forma de vida', 'Franco Vaccarini', 'Editorial desconocida', '9789875733077', '2009', 'No disponible', 'Ficción Niños', 1, 1, 'no_hay_portada.jpg'),
(219, 'El libro de los chicos enamorados', 'Elsa Bornemann, Elsa Isabel Bornemann', 'Norma', '9789580434542', '1996', 'A collection of love poems for teenagers.', 'Ficción Niños', 1, 1, 'no_hay_portada.jpg'),
(220, 'Frutas de estación', 'No disponible', 'Editorial desconocida', '9789871652112', '2010', 'No disponible', 'Ficción Niños', 1, 1, 'no_hay_portada.jpg'),
(221, 'Noticias frescas y otras aventuras', 'Emma Thomson', 'Beascoa Ediciones', '9788448822156', '2005-05', 'No disponible', 'Ficción Niños', 2, 2, 'http://books.google.com/books/content?id=ppJdAAAACAAJ&printsec=frontcover&img=1&zoom=1&source=gbs_api'),
(222, 'La estrella de plata y otras historias', 'Emma Thomson', 'Editorial desconocida', '9788448818623', '2003-10-24', 'El hada encantadora, Valeria Varita, y sus tres inseparables amigas viven divertidas aventuras llenas de magia.', 'Ficción Niños', 1, 1, 'http://books.google.com/books/content?id=N3bDAAAACAAJ&printsec=frontcover&img=1&zoom=1&source=gbs_api'),
